Shader.Text:

#include "utility/defaultUniforms.glsl"
attribute vec3 pos;
attribute vec3 normal;
attribute vec2 texcoord0;
#if Skinning
attribute vec4 jointindex;
attribute vec4 jointweight;
uniform mat4 skinMat[50];
#endif
varying vec2 texcoord;
varying vec3 worldPos;
varying vec3 worldNormal;
void main()
{
	// Transformation
	texcoord = texcoord0;
	vec4 translated = transMat * vec4(pos, 1.0);
	worldPos = translated.xyz / translated.w;
	translated = transMat * vec4(normal, 1.0);
	worldNormal = translated.xyz / translated.w;
	#if Skinning
	// Skinning
	mat4 mat = mat4(0.0);
	for (int i = 0; i < 4; i++)
	{
		mat += skinMat[int(jointindex[i])] * jointweight[i];
	}
	vec4 skinpos = mat * vec4(pos, 1.0);
	gl_Position = worldMat * skinpos;
	#else
	gl_Position = worldMat * vec4(pos, 1.0);
	#endif
}


varying vec2 texcoord;
uniform sampler2D tex;
varying vec3 color;
void main()
{
	gl_FragColor = texture2D(tex, texcoord);
}


varying vec2 texcoord;
uniform sampler2D tex;
varying vec3 color;
uniform vec3 lightPos;
uniform float radius;
varying vec3 worldPos;
varying vec3 worldNormal;
void main()
{
	vec3 lightVec = lightPos - worldPos;
	float lightDist = length(lightVec);
	lightVec /= lightDist;
	// Compute attenuation
	lightDist = lightDist / radius;
	float attenuation = max(dot(lightVec, worldNormal), 1.0) * (1.0 - lightDist * lightDist);
	gl_FragColor = texture2D(tex, texcoord) * attenuation;
}
